
public class 문자열_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="Hello ";
		String s2="Java!!";
		//concat은 문자열을 결합하는데 사용하는 것
		System.out.println(s1+s2);// 사용빈도가 많다
		System.out.println(s1.concat(s2));//
		//문자열 결합 문법  || , concat(s1,s2) => 오라클에서 사용하는 문법
		// 파이썬은 5/2= 2.5 , 5//2=2
	}

}
